"""
Database module
"""

import pickle, json, csv, os, shutil, random

class SpoolDB(dict):
    ''' Persistent database with the dictionary API as it inherits from the python dict.

    Output file format available is pickle and json.
    '''

    def __init__(self, filename=None, data={}, flag='c', mode=None, format='pickle', *args, **kwds):
        """ The spooldb constructor.
        """

        self.flag = flag                    # r=readonly, c=create, or n=new
        self.mode = mode                    # None or an octal triple like 0644
        self.format = format                # 'csv', 'json', or 'pickle'
        self.filename = filename

        self.__format_validator__(self.format)

        dict.__init__(self, data, *args, *kwds)
        if filename != None and flag != 'n' and os.access(filename, os.R_OK):
                fileobj = None
                if format == "csv":
                    fileobj = open(filename, "r", newline="")
                else:
                    fileobj = open(filename, "rb" if self.format == "pickle" else "r")
                with fileobj:
                    self.load(fileobj)
        
        # old code, silent pls
        # super(dict).__init__(*args, **kwds)
        # dict.__init__(self, *args, **kwds)

    def sync(self):
        'Write dict to disk'
        import datetime

        if not self.filename:
            raise ValueError("Filename is not set")

        if self.flag == 'r':
            return
        filename = self.filename
        tempname = ""
        random.seed(f"{str(os.urandom(32))}{str(datetime.datetime.now())}")

        # loop until that fulename does not exist
        while os.path.exists(tempname or filename):
            tempname = filename + ".tmp-" + str(random.randint(0, 9999999))

        fileobj = None
        if self.format == "csv":
            fileobj = open(tempname, "w", newline="")
        else:
            fileobj = open(tempname, 'wb' if self.format=='pickle' else 'w')
        try:
            with fileobj:
                self.dump(fileobj)
        except Exception:
            os.remove(tempname)
            raise
        shutil.move(tempname, self.filename)    # atomic commit
        if self.mode is not None:
            os.chmod(self.filename, self.mode)

    def close(self):
        ''' Close the database.'''
        self.sync()

    def __enter__(self):
        return self

    def __exit__(self, *exc_info):
        self.close()

    def __del__(self):
        self.close()

    def __deepcopy__(self, requesteddeepcopy):
        import copy
        return copy.deepcopy(dict(self))

    def setfname(self, filename):
        '''Set the filename

        Set to None to make a memory db
        '''
        self.filename = filename

    def getfname(self):
        '''Get the filename
        
        If null (more like None), then it is a memory db
        '''
        return self.filename

    def getfmt(self):
        '''Get the format
        
        '''
        return self.format

    def setfmt(self, format):
        '''Set the format
        
        '''
        # validate format
        self.__format_validator__(format)
        self.format = format

    def dump(self, fileobj):
        'Dump the dict to disk'
        # validate the format
        self.__format_validator__(self.format)

        if self.format == 'csv':
            csv.writer(fileobj).writerows(self.items())
        elif self.format == 'json':
            json.dump(self, fileobj, separators=(',', ':'), indent=4)
        elif self.format == 'pickle':
            pickle.dump(dict(self), fileobj, 2)
        else:
            raise NotImplementedError('Unknown format: ' + repr(self.format))

    def load(self, fileobj):
        'load dict from disk'
        # validate the format
        self.__format_validator__(self.format)

        # try formats from most restrictive to least restrictive
        for loaderset in ((pickle.load, "pickle"), (json.load, "json"), (csv.reader, "csv")):
            fileobj.seek(0)
            try:
                if loaderset[1] != "csv":
                    return self.update(loaderset[0](fileobj))
                else:
                    d = dict()
                    for k, v in loaderset[0](fileobj):
                        d[k] = v
                    return self.update(d)
            except Exception as e:
                # print(f"{str(loaderset)}: {str(e)}")
                # no say this
                pass
        raise ValueError('File not in a supported format')

    def __format_validator__(self, format):
        """Validate format"""
        if format not in ('csv', 'json', 'pickle'):
            raise ValueError('Unknown format: ' + repr(format))
        else:
            return format